# üöÄ Website TopUp Game - Auto Setup Script (Windows)\n# This script will automatically setup the development environment\n\nWrite-Host \"üéÆ Website TopUp Game - Auto Setup\" -ForegroundColor Cyan\nWrite-Host \"=====================================`n\" -ForegroundColor Cyan\n\n# Check if Node.js is installed\ntry {\n    $nodeVersion = node --version\n    Write-Host \"‚úÖ Node.js version: $nodeVersion\" -ForegroundColor Green\n} catch {\n    Write-Host \"‚ùå Node.js is not installed. Please install Node.js 18+ first.\" -ForegroundColor Red\n    Write-Host \"   Download from: https://nodejs.org/\" -ForegroundColor Yellow\n    exit 1\n}\n\n# Check if npm is installed\ntry {\n    $npmVersion = npm --version\n    Write-Host \"‚úÖ npm version: $npmVersion`n\" -ForegroundColor Green\n} catch {\n    Write-Host \"‚ùå npm is not installed.\" -ForegroundColor Red\n    exit 1\n}\n\n# Install dependencies\nWrite-Host \"üì¶ Installing dependencies...\" -ForegroundColor Yellow\nnpm install\n\nif ($LASTEXITCODE -eq 0) {\n    Write-Host \"‚úÖ Dependencies installed successfully!`n\" -ForegroundColor Green\n} else {\n    Write-Host \"‚ùå Failed to install dependencies.\" -ForegroundColor Red\n    exit 1\n}\n\n# Create .env.local if it doesn't exist\nif (!(Test-Path \".env.local\")) {\n    Write-Host \"‚öôÔ∏è  Creating environment file...\" -ForegroundColor Yellow\n    \n    if (Test-Path \".env.example\") {\n        Copy-Item \".env.example\" \".env.local\"\n    } else {\n        \"# Environment variables`nNEXT_PUBLIC_APP_NAME=TopUp Game Website\" | Out-File -FilePath \".env.local\" -Encoding UTF8\n    }\n    \n    Write-Host \"‚úÖ Environment file created!`n\" -ForegroundColor Green\n}\n\n# Check if images directory exists\nif (!(Test-Path \"public\\images\")) {\n    Write-Host \"üìÅ Creating images directories...\" -ForegroundColor Yellow\n    New-Item -ItemType Directory -Path \"public\\images\\games\" -Force | Out-Null\n    New-Item -ItemType Directory -Path \"public\\images\\banners\" -Force | Out-Null\n    New-Item -ItemType Directory -Path \"public\\images\\providers\" -Force | Out-Null\n    Write-Host \"‚úÖ Image directories created!`n\" -ForegroundColor Green\n}\n\n# Run type checking\nWrite-Host \"üîç Running type check...\" -ForegroundColor Yellow\nnpm run type-check\n\nif ($LASTEXITCODE -eq 0) {\n    Write-Host \"‚úÖ Type check passed!`n\" -ForegroundColor Green\n} else {\n    Write-Host \"‚ö†Ô∏è  Type check warnings found (this is normal)`n\" -ForegroundColor Yellow\n}\n\n# Run build test\nWrite-Host \"üî® Testing build...\" -ForegroundColor Yellow\nnpm run build\n\nif ($LASTEXITCODE -eq 0) {\n    Write-Host \"‚úÖ Build test successful!`n\" -ForegroundColor Green\n} else {\n    Write-Host \"‚ùå Build failed. Please check the errors above.\" -ForegroundColor Red\n    exit 1\n}\n\nWrite-Host \"üéâ Setup completed successfully!\" -ForegroundColor Green\nWrite-Host \"`nüìã Next steps:\" -ForegroundColor Cyan\nWrite-Host \"   1. Replace images in public/images/ with your custom images\" -ForegroundColor White\nWrite-Host \"   2. Update game data in app/page.tsx\" -ForegroundColor White\nWrite-Host \"   3. Run 'npm run dev' to start development server\" -ForegroundColor White\nWrite-Host \"   4. Visit http://localhost:3000 to see your website\" -ForegroundColor White\nWrite-Host \"`nüöÄ Ready to deploy:\" -ForegroundColor Cyan\nWrite-Host \"   - Run 'vercel --prod' to deploy to Vercel\" -ForegroundColor White\nWrite-Host \"   - Or follow DEPLOYMENT.md for other hosting options\" -ForegroundColor White\nWrite-Host \"`nüéÆ Happy coding!\" -ForegroundColor Magenta\n\n# Ask if user wants to start dev server\n$startDev = Read-Host \"`nWould you like to start the development server now? (y/n)\"\nif ($startDev -eq \"y\" -or $startDev -eq \"Y\" -or $startDev -eq \"yes\") {\n    Write-Host \"`nüöÄ Starting development server...\" -ForegroundColor Green\n    Write-Host \"Opening http://localhost:3000 in your browser...\" -ForegroundColor Yellow\n    Start-Process \"http://localhost:3000\"\n    npm run dev\n}\n