#!/bin/bash\n\n# 🚀 Website TopUp Game - Auto Setup Script\n# This script will automatically setup the development environment\n\necho \"🎮 Website TopUp Game - Auto Setup\"\necho \"=====================================\\n\"\n\n# Check if Node.js is installed\nif ! command -v node &> /dev/null; then\n    echo \"❌ Node.js is not installed. Please install Node.js 18+ first.\"\n    echo \"   Download from: https://nodejs.org/\"\n    exit 1\nfi\n\necho \"✅ Node.js version: $(node --version)\"\n\n# Check if npm is installed\nif ! command -v npm &> /dev/null; then\n    echo \"❌ npm is not installed.\"\n    exit 1\nfi\n\necho \"✅ npm version: $(npm --version)\\n\"\n\n# Install dependencies\necho \"📦 Installing dependencies...\"\nnpm install\n\nif [ $? -eq 0 ]; then\n    echo \"✅ Dependencies installed successfully!\\n\"\nelse\n    echo \"❌ Failed to install dependencies.\"\n    exit 1\nfi\n\n# Create .env.local if it doesn't exist\nif [ ! -f \".env.local\" ]; then\n    echo \"⚙️  Creating environment file...\"\n    cp .env.example .env.local 2>/dev/null || echo \"# Environment variables\\nNEXT_PUBLIC_APP_NAME=TopUp Game Website\" > .env.local\n    echo \"✅ Environment file created!\\n\"\nfi\n\n# Check if images directory exists\nif [ ! -d \"public/images\" ]; then\n    echo \"📁 Creating images directories...\"\n    mkdir -p public/images/games\n    mkdir -p public/images/banners\n    mkdir -p public/images/providers\n    echo \"✅ Image directories created!\\n\"\nfi\n\n# Run type checking\necho \"🔍 Running type check...\"\nnpm run type-check\n\nif [ $? -eq 0 ]; then\n    echo \"✅ Type check passed!\\n\"\nelse\n    echo \"⚠️  Type check warnings found (this is normal)\\n\"\nfi\n\n# Run build test\necho \"🔨 Testing build...\"\nnpm run build\n\nif [ $? -eq 0 ]; then\n    echo \"✅ Build test successful!\\n\"\nelse\n    echo \"❌ Build failed. Please check the errors above.\"\n    exit 1\nfi\n\necho \"🎉 Setup completed successfully!\"\necho \"\\n📋 Next steps:\"\necho \"   1. Replace images in public/images/ with your custom images\"\necho \"   2. Update game data in app/page.tsx\"\necho \"   3. Run 'npm run dev' to start development server\"\necho \"   4. Visit http://localhost:3000 to see your website\"\necho \"\\n🚀 Ready to deploy:\"\necho \"   - Run 'vercel --prod' to deploy to Vercel\"\necho \"   - Or follow DEPLOYMENT.md for other hosting options\"\necho \"\\n🎮 Happy coding!\"\n